<?php
	class olomoIcons {
		private $icon_code = array();	 
		public function __construct() {
			$this->icon_code['todayTime'] = "data:image/png;base64,";
			$this->icon_code['whiteMapMarker'] = "data:image/png;base64,";
			$this->icon_code['whiteMapMarkerFill'] = "data:image/png;base64,";
			$this->icon_code['building'] = "data:image/png;base64,";
		  $this->icon_code['reservation'] = "data:image/png;base64,";
			$this->icon_code['pencil'] = "data:image/png;base64,";
			$this->icon_code['house'] = "data:image/png;base64,";
			$this->icon_code['share'] = "data:image/png;base64,";	
			$this->icon_code['checked'] = "data:image/png;base64,";
			$this->icon_code['mapMarker'] = "data:image/png;base64,";
			$this->icon_code['mapMarkerGrey'] = "data:image/png;base64,";
			$this->icon_code['message'] = "data:image/png;base64,";
			$this->icon_code['globe'] = "data:image/png;base64,";
			$this->icon_code['clock'] = "data:image/png;base64,";
			$this->icon_code['contacts'] = "data:image/png;base64,";
		 }

		 // Returns colored string
		 public function olomo_icon($icon_name = null) {
			 $icon = "";
			 // Check if given foreground color found
			 if (isset($this->icon_code[$icon_name])) {
				$icon .= $this->icon_code[$icon_name];
			 }
			 return $icon;
		 }
	 }